// src/pages/Home.js
import React from "react";
import herosection from "./assets/HeroSection/herosection.png";

const Home = () => {
  return (
    <div className="relative h-screen flex flex-col items-center justify-center py-12 bg-gradient-to-b from-purple-600 to-blue-600">
      <div className="flex flex-col items-center justify-center p-6 bg-black bg-opacity-50 rounded-lg shadow-lg mb-10">
        <div
          className="bg-center mb-10"
          style={{
            backgroundImage: `url(${herosection})`,
            backgroundSize: "contain", // Ensures the hero section image fits nicely
            backgroundRepeat: "no-repeat",
            width: "200px", // Adjust width as needed
            height: "100px", // Adjust height as needed
            margin: "0 auto", // Centers the image horizontally
          }}
        ></div>
        <h1 className="text-white text-3xl font-bold mt-4">Welcome to Iskon</h1>
        <p className="text-white mt-2 text-lg">
          Discover a world of opportunities and connections.
        </p>
        <button className="mt-4 bg-yellow-500 text-black font-semibold py-2 px-4 rounded hover:bg-yellow-400 transition duration-300">
          Get Started
        </button>
      </div>

      {/* Additional section below the hero section */}
      <div className="flex flex-col items-center text-center p-6 bg-white rounded-lg shadow-md">
        <h2 className="text-xl font-bold mb-4">Explore Our Services</h2>
        <p className="text-gray-700 mb-4">
          We offer a variety of services to help you connect and grow.
        </p>
        <button className="bg-blue-500 text-white font-semibold py-2 px-4 rounded hover:bg-blue-400 transition duration-300">
          Learn More
        </button>
      </div>
    </div>
  );
};

export default Home;
