// src/pages/Home.js
import React from "react";
import banner01 from "./assets/Banner01.jpg";
import herosection from "./assets/HeroSection/herosection.png";

const Home = () => {
  return (
    <div
      className="h-screen bg-cover bg-center relative"
      style={{
        backgroundImage: `url(${banner01})`,
        backgroundSize: "cover", // Ensures the image covers the whole div
        backgroundPosition: "center", // Centers the image
      }}
    >
      <div className="flex items-center justify-center h-full bg-black bg-opacity-50 absolute bottom-0 w-full">
        <div className="text-center p-4">
          <div
            className="bg-center"
            style={{
              backgroundImage: `url(${herosection})`,
              backgroundSize: "contain", // Ensures the hero section image fits nicely
              backgroundRepeat: "no-repeat",
              width: "200px", // Adjust width as needed
              height: "100px", // Adjust height as needed
              margin: "0 auto", // Centers the image horizontally
            }}
          ></div>
          <h1 className="text-white text-2xl font-bold mt-4">
            Welcome to Iskon
          </h1>
        </div>
      </div>
    </div>
  );
};

export default Home;
