import React from "react";

const DonationPage = () => {
  return (
    <div className="min-h-screen bg-gray-100">
      {/* Heading Section */}
      <div className="text-center py-10 bg-white shadow-lg">
        <h1 className="text-4xl font-bold text-gray-800">Donation</h1>
        <p className="mt-4 text-lg text-gray-600 px-6 md:px-48">
          Krishna once said that good deeds made, gifted, frugal, and done
          without faith are shown as no or “assat.” Therefore, neither here nor
          below is useful. Let us take a step closer to the devotion of Kanha
          and do our bit to help others in need.
        </p>
      </div>

      {/* Donation Categories */}
      <div className="container mx-auto py-12 px-4">
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8">
          {/* Prasadam Donation */}
          <DonationCard
            title="Prasadam Donation"
            description="Donate to provide prasadam to devotees and the needy."
            link="#"
          />

          {/* Janmashtami Seva */}
          <DonationCard
            title="Janmashtami Seva"
            description="Participate in the grand festival of Janmashtami by donating for the seva."
            link="#"
          />

          {/* Food For Life */}
          <DonationCard
            title="Food For Life"
            description="Donate to our Food For Life program and help serve the community."
            link="#"
          />

          {/* Temple Seva */}
          <DonationCard
            title="Temple Seva"
            description="Contribute towards the maintenance and development of the temple."
            link="#"
          />

          {/* Nitya Seva */}
          <DonationCard
            title="Nitya Seva"
            description="Support the daily rituals and offerings by donating to the Nitya Seva."
            link="#"
          />
        </div>
      </div>
    </div>
  );
};

// Donation Card Component
const DonationCard = ({ title, description, link }) => {
  return (
    <div className="bg-white rounded-lg shadow-lg p-6 hover:shadow-xl transition-shadow duration-300">
      <h3 className="text-xl font-bold text-gray-800 mb-4">{title}</h3>
      <p className="text-gray-600 mb-6">{description}</p>
      <a
        href={link}
        className="inline-block bg-blue-600 text-white font-semibold px-6 py-2 rounded-full hover:bg-blue-700 transition-colors"
      >
        Donate Now
      </a>
    </div>
  );
};

export default DonationPage;
